css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    --rootclr: #404042, #7acac7, #8f8f8f;
}

.p1{
    height: 330px;
    width: 100%;
}

.c1{
    height: 60%;
    width: 100%;
    background-color: #404042;
    color: white;
    font-size: 32px;
    text-transform: uppercase;
    display: flex;
    justify-content: center;
    align-items: center;
}

.c11{
    height: 40%;
    width: 100%;
    background-color: #7acac7;
}
.p2{
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
}
.c2{
    width: 220px;
    height: 220px;
    overflow: hidden;
    border: 13px white solid;
    border-radius: 50%;
    position: absolute;
    top: -180px;
    object-fit: cover;
}

.c2>img{
    width: 100%;
}

.p3{
    margin-top: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.p3>h1{
    letter-spacing: 2px;
    margin-bottom: 0;
}
.p3>h1>span{
    font-weight: bold;

}
.p3>h2{
    margin-top: 5px;
    font-size: 22px;
    color: #7acac7;
}

.p4{
    margin-top: 40px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 0 20%;
}
.p4 h3,
.p5 h3{
    margin-top: 5px;
    font-size: 26px;
    letter-spacing: 2px;
}
.c4{
    width: 100%;
    display: flex;
}
.c41{
    width: 40%;
}
.c41>h3{
    color: #7acac7;
    font-weight: bold;
}
.c42>h3{
    color: #8f8f8f;
}
.p5{
    margin-top: 25px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 0 20%;
}
.c5{
    width: 100%;
    display: flex;
}
.c51{
    width: 40%;
}
.c51>h3{
    color: #7acac7;
    font-weight: bold;
}
.c52>h3{
    color: #8f8f8f;
}
.p6{
    margin-top: 60px;
    width: 100%;
    height: 145px;
    background-color: #404042;
}
.c6{
    height: 50%;
    width: 100%;
    background-color: #404042;
    color: white;
    font-size: 32px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 40px;
}

.c60{
    height: 50%;
    width: 100%;
    background-color: #7acac7;
}

.c61{
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
}
.c61>h3{
    font-size: 28px;
}
.c62{
    background-color: #7acac7;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
}
.c62>i{
    font-size: 25px;
}
